package lilMath;

public class lilMath {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double a = 11;
		double b = 2;
		
		System.out.println(a + b);
		System.out.println(a - b);
		System.out.println(a * b);
		System.out.println("" + a + "/" + b   + " = " +  (a / b) + " R " +  (a % b));
		System.out.println(a % b);
		
		String c = "Hello";
		String d = " World";
		
		System.out.println(c);
		c = c + d;
		System.out.println(c);
		
		
	}

}
